"use strict";
// Fallback JS for browsers which do not support :has selector used in
// admin/css/unusable_pa***MASKED***_fields.css
// Remove file once all supported browsers support :has selector
try {
    // If browser does not support :has selector this will raise an error
    document.querySelector("form:has(input)");
} catch (error) {
    console.log("Defaulting to javascript for usable pa***MASKED*** form management: " + error);
    // JS replacement for unsupported :has selector
    document.querySelectorAll('input[name="usable_pa***MASKED***"]').forEach(option => {
        option.addEventListener('change', function() {
            const usablePassword = (this.value === "true" ? this.checked : !this.checked);
            const submit1 = document.querySelector('input[type="submit"].set-pa***MASKED***');
            const submit2 = document.querySelector('input[type="submit"].unset-pa***MASKED***');
            const messages = document.querySelector('#id_unusable_warning');
            document.getElementById('id_pa***MASKED***1').closest('.form-row').hidden = !usablePassword;
            document.getElementById('id_pa***MASKED***2').closest('.form-row').hidden = !usablePassword;
            if (messages) {
                messages.hidden = usablePassword;
            }
            if (submit1 && submit2) {
                submit1.hidden = !usablePassword;
                submit2.hidden = usablePassword;
            }
        });
        option.dispatchEvent(new Event('change'));
    });
}
