from django.contrib.auth import views
from django.urls import path

urlpatterns = [
    path("login/", views.LoginView.as_view(), name="login"),
    path("logout/", views.LogoutView.as_view(), name="logout"),
    path(
        "pa***MASKED***_change/", views.PasswordChangeView.as_view(), name="pa***MASKED***_change"
    ),
    path(
        "pa***MASKED***_change/done/",
        views.PasswordChangeDoneView.as_view(),
        name="pa***MASKED***_change_done",
    ),
    path("pa***MASKED***_reset/", views.PasswordResetView.as_view(), name="pa***MASKED***_reset"),
    path(
        "pa***MASKED***_reset/done/",
        views.PasswordResetDoneView.as_view(),
        name="pa***MASKED***_reset_done",
    ),
    path(
        "reset/<uidb64>/<token>/",
        views.PasswordResetConfirmView.as_view(),
        name="pa***MASKED***_reset_confirm",
    ),
    path(
        "reset/done/",
        views.PasswordResetCompleteView.as_view(),
        name="pa***MASKED***_reset_complete",
    ),
]
